mapper.sql:
FROM tradBooking a
WHERE 1=1
	AND tdbkDelNy = #{shDelNy}

		AND tdbkSeq = #{shValue}
		AND tdbkPayment LIKE CONCAT('%', #{shValue},'%')

	ORDER BY a.tdbkSeq desc

mapper.insert:
    insert INTO tradBooking (
		tdbkBookingDate
		,tdbkPayment
		,infrMember_ifmmSeq
		,tradTimeTable_tdttSeq
		,tdbkTotalCost
	)
	VALUES (
		now()
	    ,#{tdbkPayment}
	    ,#{ifmmSeq}
	    ,#{tdttSeq}
	    ,#{tdbkTotalCost}
	)

			SELECT last_insert_id()
