mapper.insert:
INSERT INTO infrCode (
	cdName
	,cdUseNy
	,cdDelNy
	,infrCodeGroup_cgSeq as cgSeq
)
VALUES(
	#{cdName}
	,#{cdUseNy}
	,#{cdDelNy}
	,#{infrCodeGroup_cgSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrCode
SET
 infrCodeGroup_cgSeq as cgSeq = #{infrCodeGroup_cgSeq}
 ,cdName = #{cdName}
 ,cdUseNy = #{cdUseNy}
 ,cdDelNy = #{cdDelNy}
WHERE 1=1
	a. cdSeq = #{cdSeq}
