mapper.sql:
FROM infrCode a JOIN infrCodeGroup b on a.infrCodeGroup_cgSeq = b.cgSeq WHERE 1=1 AND a.cdDelNy = #{shDelNy}
AND a.cdSeq = #{shValue} AND a.cdName LIKE CONCAT('%', #{shValue},'%')
ORDER BY a.cdSeq

mapper.insert:
INSERT INTO infrCode (
	cdName
	,cdUseNy
	,cdDelNy
	,infrCodeGroup_cgSeq
)
VALUES(
	#{cdName}
	,#{cdUseNy}
	,#{cdDelNy}
	,#{infrCodeGroup_cgSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrCode
SET
 infrCodeGroup_cgSeq = #{infrCodeGroup_cgSeq}
 ,cdName = #{cdName}
 ,cdUseNy = #{cdUseNy}
 ,cdDelNy = #{cdDelNy}
WHERE 1=1
	cdSeq = #{cdSeq}
