mapper.sql:
FROM infrCodeGroup a
WHERE 1=1
	AND cgDelNy = #{shDelNy}

		AND cgSeq = #{shValue}
		AND cgName LIKE CONCAT('%',#{shValue},'%')
		AND cgNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrCodeGroup (
	cgName
    ,cgNameEng
    ,cgUseNy
    ,cgDelNy
)
VALUES (
	#{cgName}
    ,#{cgNameEng}
    ,#{cgUseNy}
    ,#{cgDelNy}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrCodeGroup 
SET
	cgName = #{cgName}
	,cgNameEng = #{cgNameEng}
	,cgUseNy = #{cgUseNy}
	,cgDelNy = #{cgDelNy}

WHERE
	cgSeq = #{cgSeq}
