mapper.insert:
INSERT INTO infrMember (
	ifmmId
	,ifmmPassword
	,ifmmName
	,ifmmGender
	,ifmmEmailAddress
	,ifmmEmailDomain
	,ifmmPhone
	,ifmmDob
	,ifmmGrade
	,ifmmDelNy
	,ifmmUseNy
)
VALUES(
	#{ifmmId}
	,#{ifmmPassword}
	,#{ifmmName}
	,#{ifmmGender}
	,CONCAT(#{ifmmEmailAddress},'@',(SELECT cc.cdName FROM infrCode cc WHERE cc.cdSeq = #{ifmmEmailDomain}))
	,#{ifmmPhone}
	,#{ifmmDob}
	,4
	,0
	,1
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrMember
SET
	ifmmSeq = #{ifmmSeq}
	,ifmmPassword = #{ifmmPassword}
	,ifmmName = #{ifmmName}
	,ifmmGender = #{ifmmGender}
	,ifmmGrade = #{ifmmGrade}
	,ifmmEmailAddress =#{ifmmEmailAddress}
	,ifmmEmailDomain=#{ifmmEmailDomain}
	,ifmmPhone=#{ifmmPhone}
	,ifmmDob=#{ifmmDob}
	,ifmmEmailNy=#{ifmmEmailNy}
	,ifmmSmsNy=#{ifmmSmsNy}
	,ifmmDobLoginNy=#{ifmmDobLoginNy}
	,ifmmSnsLogin=#{ifmmSnsLogin}
	,ifmmDelNy=#{ifmmDelNy}
	,ifmmUseNy=#{ifmmUseNy}
WHERE 1=1
AND	ifmmSeq = #{ifmmSeq}
