mapper.insert:
INSERT INTO infrMember (
	ifmmSeq
	,ifmmPassword
	,ifmmName
	,ifmmGender
	,ifmmGrade
	,ifmmEmailAddress
	,ifmmEmailDomain
	,ifmmPhone
	,ifmmDob
	,ifmmEmailNy
	,ifmmSmsNy
	,ifmmDobLoginNy
	,ifmmSnsLogin
	,ifmmDelNy
	,ifmmUseNy
)
VALUES(
	#{ifmmSeq}
	,#{ifmmPassword}
	,#{ifmmName}
	,#{ifmmGender}
	,#{ifmmGrade}
	,#{ifmmEmailAddress}
	,#{ifmmEmailDomain}
	,#{ifmmPhone}
	,#{ifmmDob}
	,#{ifmmEmailNy}
	,#{ifmmSmsNy}
	,#{ifmmDobLoginNy}
	,#{ifmmSnsLogin}
	,#{ifmmDelNy}
	,#{ifmmUseNy}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrMember
SET
	ifmmSeq = #{ifmmSeq}
	,ifmmPassword = #{ifmmPassword}
	,ifmmName = #{ifmmName}
	,ifmmGender = #{ifmmGender}
	,ifmmGrade = #{ifmmGrade}
	,ifmmEmailAddress =#{ifmmEmailAddress}
	,ifmmEmailDomain=#{ifmmEmailDomain}
	,ifmmPhone=#{ifmmPhone}
	,ifmmDob=#{ifmmDob}
	,ifmmEmailNy=#{ifmmEmailNy}
	,ifmmSmsNy=#{ifmmSmsNy}
	,ifmmDobLoginNy=#{ifmmDobLoginNy}
	,ifmmSnsLogin=#{ifmmSnsLogin}
	,ifmmDelNy=#{ifmmDelNy}
	,ifmmUseNy=#{ifmmUseNy}
WHERE 1=1
	ifmmSeq = #{ifmmSeq}
