mapper.sql:
FROM tradMovie a
WHERE 1=1
AND tdmvDelNy = #{shDelNy}

	AND a.tdmvSeq = #{shValue}
	AND a.tdmvMovieTitle LIKE CONCAT('%', #{shValue},'%')

mapper.insert:
INSERT INTO tradMovie(
	tdmvMovieTitle
	,tdmvTitleEng
	,tdmvStory
	,tdmvShowType
	,tdmvDirector
	,tdmvCast
	,tdmvGenres
	,tdmvRunningTime
	,tdmvAge
	,tdmvReleaseDate
	,tdmvTrailer
	,tdmvRegDate
)
VALUES(
	#{tdmvMovieTitle}
	,#{tdmvTitleEng}
	,#{tdmvStory}
	,#{tdmvShowType}
	,#{tdmvDirector}
	,#{tdmvCast}
	,#{tdmvGenres}
	,#{tdmvRunningTime}
	,#{tdmvAge}
	,#{tdmvReleaseDate}
	,#{tdmvTrailer}
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE tradMovie
SET
	tdmvMovieTitle = #{tdmvMovieTitle}
	,tdmvTitleEng = #{tdmvTitleEng}
	,tdmvStory = #{tdmvStory}
	,tdmvDirector = #{tdmvDirector}
	,tdmvCast = #{tdmvCast}
	,tdmvGenres = #{tdmvGenres}
	,tdmvRunningTime = #{tdmvRunningTime}
	,tdmvAge = #{tdmvAge}
	,tdmvReleaseDate = #{tdmvReleaseDate}
	,tdmvTrailer = #{tdmvTrailer}
	,tdmvModDate = now() 
WHERE
	tdmvSeq = #{tdmvSeq}
