mapper.sql:
FROM tradMovie a
WHERE 1=1
AND tdmvDelNy = #{shDelNy}

	AND a.tdmvSeq = #{shValue}
	AND a.tdmvMovieTitle LIKE CONCAT('%', #{shValue},'%')

mapper.insert:
INSERT INTO tradMovie(
	tdmvMovieTitle
	,tdmvTitleEng
	,tdmvStory
	,tdmvShowType
	,tdmvDirector
	,tdmvCast
	,tdmvGenres
	,tdmvRunningTime
	,tdmvAge
	,tdmvReleaseDate
	,tdmvTrailer
	,tdmvRegDate
	,tdmvState
	,tdmvPercent
)
VALUES(
	#{tdmvMovieTitle}
	,#{tdmvTitleEng}
	,#{tdmvStory}
	,#{tdmvShowType}
	,#{tdmvDirector}
	,#{tdmvCast}
	,#{tdmvGenres}
	,#{tdmvRunningTime}
	,#{tdmvAge}
	,#{tdmvReleaseDate}
	,#{tdmvTrailer}
	,now()
	,#{tdmvState}
	,#{tdmvPercent}
)

	SELECT last_insert_id()

mapper.update:
UPDATE tradMovie
SET
	tdmvMovieTitle = #{tdmvMovieTitle}
	,tdmvTitleEng = #{tdmvTitleEng}
	,tdmvStory = #{tdmvStory}
	,tdmvDirector = #{tdmvDirector}
	,tdmvCast = #{tdmvCast}
	,tdmvGenres = #{tdmvGenres}
	,tdmvRunningTime = #{tdmvRunningTime}
	,tdmvAge = #{tdmvAge}
	,tdmvReleaseDate = #{tdmvReleaseDate}
	,tdmvTrailer = #{tdmvTrailer}
	,tdmvModDate = now() 
	,tdmvState = #{tdmvState}
WHERE
	tdmvSeq = #{tdmvSeq}

mapper.insert:
insert into tradMovieUpload(
	defaultNy
    ,type
    ,sort
    ,originalName
    ,uuIdName
    ,ext
    ,size
    ,delNy
    ,regDateTime
    ,pseq
    ,upPath
)
values (
	#{defaultNy}
    ,#{type}
    ,#{sort}
    ,#{originalName}
    ,#{uuIdName}
    ,#{ext}
    ,#{size}
    ,#{delNy}
    ,now()
    ,#{pseq}
    ,#{upPath}
)

	SELECT last_insert_id()

insert into tradMovieComment (
	tdmcCreatDate
    ,infrMember_ifmmSeq
    ,tradMovie_tdmvSeq
    ,tdmcContent
    ,tdmcRate
)
values (
	now()
    ,#{ifmmSeq}
    ,#{tdmvSeq}
    ,#{tdmcContent}
    ,#{tdmcRate}
)
