mapper.sql:
FROM tradTheater a WHERE 1=1 AND tdthDelNy = #{shDelNy}
AND tdthSeq = #{shValue} AND tdthRegion LIKE CONCAT('%',#{shValue},'%') AND tdthBranch LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.tdthSeq

mapper.insert:
INSERT INTO tradTheater(
	tdthRegion
	,tdthBranch
	,tdthBranchEng
	,tdthAddress
	,tdthZipcode
	,tdthDelNy
	,tdthUseNy
) 
VALUES(
	#{tdthRegion}
	,#{tdthBranch}
	,#{tdthBranchEng}
	,#{tdthAddress}
	,#{tdthZipcode}
	,#{tdthDelNy}
	,#{tdthUseNy}
)

	SELECT last_insert_id()

mapper.update:
UPDATE tradTheater
SET
	tdthRegion = #{tdthRegion}
	,tdthBranch = #{tdthBranch}
	,tdthBranchEng = #{tdthBranchEng}
	,tdthAddress = #{tdthAddress}
	,tdthZipcode = #{tdthZipcode}
	,tdthDelNy = #{tdthDelNy}
	,tdthUseNy = #{tdthUseNy}
WHERE
	tdthSeq = #{tdthSeq}
