mapper.sql:
FROM tradTheaterPlex a JOIN tradTheater b on b.tdthSeq = a.tradTheater_tdthSeq WHERE 1=1 AND tdpxDelNy = #{shDelNy}
AND a.tdpxSeq = #{shValue} AND a.tdpxPlexName LIKE CONCAT('%',#{shValue},'%') AND b.tdthBranch LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.tdpxSeq

mapper.insert:
INSERT INTO tradTheaterPlex (
	tdpxPlexName
	,tdpxPlexName
	,tdpxSittingColNum
	,tdpxSittingRowNum
	,tdpxDelNy
	,tdpxUseNy
	,tradTheater_tdthSeq as tdthSeq
)
VALUES(
	#{tdpxPlexName}
	,#{tdpxPlexName}
	,#{tdpxSittingColNum}
	,#{tdpxSittingRowNum}
	,0
	,1
	,#{tdthSeq}
)

	SELECT last_insert_id()
