mapper.sql:
FROM tradTheaterPrice
JOIN tradTheater b on b.tdthSeq = a.tradTheater_tdthSeq
WHERE 1=1
	AND thprDelNy = #{thprDelNy}

		AND thprSeq = #{shValue}
		AND tdthBranch = #{shValue}

ORDER BY a.	thprSeq

mapper.insert:
INSERT INTO tradTheaterPrice (
	tradTheater_tdthSeq 
	,thprWeekendNy
	,thprMorningNy
	,thprTeenagerNy
	,thprPrice
	,thprDelNy
	,thprUseNy
)
VALUES (
	#{tdthSeq} 
	,#{thprWeekendNy}
	,#{thprMorningNy}
	,#{thprTeenagerNy}
	,#{thprPrice}
	,0
	,1
)

		SELECT last_insert_id()

mapper.update:
UPDATE  tradTheaterPrice
SET
	tradTheater_tdthSeq = #{tdthSeq} 
	,thprWeekendNy = #{thprWeekendNy}
	,thprMorningNy = #{thprMorningNy}
	,thprTeenagerNy = #{thprTeenagerNy}
	,thprPrice = #{thprPrice}
	,thprDelNy = #{thprDelNy}
	,thprUseNy = #{thprUseNy}
WHERE
	 thprSeq = #{thprSeq}
