mapper.sql:
FROM tradTimeTable a JOIN tradTheater b on b.tdthSeq = a.tradTheater_tdthSeq JOIN tradTheaterPlex c on c.tdpxSeq = a.tradTheaterPlex_tdpxSeq JOIN tradMovie d on d.tdmvSeq = a.tradMovie_tdmvSeq WHERE 1=1 AND tdttDelNy = #{shDelNy}
AND a.tdttShowTime LIKE CONCAT('%',#{shValue},'%') AND d.tdmvMovieTitle LIKE CONCAT('%',#{shValue},'%') AND b.tdthBranch LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.tdttSeq

mapper.insert:
INSERT INTO tradTimeTable (
	tdttSeq
	,tdttShowTime
	,tdttMorningNy
	,tradTheater_tdthSeq
	,tradTheaterPlex_tdpxSeq
	,tradMovie_tdmvSeq
)
VALUES (
	#{tdttSeq}
	,#{tdttShowTime}
	,#{tdttMorningNy}
	,#{tradTheater_tdthSeq}
	,#{tradTheaterPlex_tdpxSeq}
	,#{tradMovie_tdmvSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE tradTimeTable
SET
	tdttShowTime  = #{tdttShowTime}
	,tdttMorningNy  = #{tdttMorningNy}
	,tradTheater_tdthSeq  = #{tdthSeq}
	,tradTheaterPlex_tdpxSeq  = #{tdpxSeq}
	,tradMovie_tdmvSeq  = #{tdmvSeq}
WHERE
	tdttSeq = #{tdttSeq}

UPDATE tradTimeTable
SET
	tdttDelNy =#{tdttDelNy}
WHERE
tdttSeq = #{tdttSeq}
